    function numFunc() {
        var x = 10;
        return {
            getNum: function() {
                return x;
            },
            setNum: function(newNum) {
                x = newNum;
            }
        };
    }

    var callFunc = numFunc();
    gs.info("Initial num: " + callFunc.getNum()); // Output should be 10
    callFunc.setNum(2);
    gs.info("Updated num: " + callFunc.getNum()); // Output should be 2
})(current, previous);
